public class ElseIFPrimerSa5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int broj = 1;
		
		if ( broj==5 ) {
			
			System.out.println("broj je jednako 5");
		}
		else if ( broj>5 && broj<10 ) {
			
			System.out.println("Broj je veci od pet i manji od 10");
		}
		else if( broj >= 10 ) {
		System.out.println("broj je veci od 10 ");
	}
	       else {
		
		System.out.println("broj je manji od 5");
	}}}

